import { createContext, useContext, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useParams } from 'react-router-dom';
import './App.css';
import UserList from './components/UserList/UserList';
import UserPage from './components/UserPage/UserPage';
import Modal from './components/Modal/Modal';

const ModalContext = createContext(null);

function App() {
  // const [modalContext, setModalContext] = useState(
  //   {
  //     sorted: false,
  //     showModal: false,
  //     filterValue: '',
  //   }
  // );
  function UserPage() {
    const { userId } = useParams(); // Получаем userId из маршрута
    return (
      <div>
        <h1>Страница пользователя: {userId}</h1>
        {/* Здесь отображаем информацию о пользователе */}
        {/* Можно загружать данные пользователя через useEffect и передавать userId */}
      </div>
    );
  }
  return (
    // <div className="App">
    //   <ModalContext.Provider
    //     value={{
    //       modalContext,
    //       setModalContext
    //     }}>
    //     <UserList />
    //   </ModalContext.Provider>
    // </div>
    <Router>
    <Routes>
      <Route element={<UserList />}/>
      <Route path="/users/:userId" element={<UserPage />} />
      {/* Другие маршруты */}
    </Routes>
  </Router>
  );
}

export default App;

export function useModal() {
  return useContext(ModalContext);
}
