import { BrowserRouter as Router, Route, Routes, useParams, useEffect, useState } from 'react-router-dom';

function UserPage() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Загрузка данных пользователя (замените на ваш метод)
    async function fetchUser() {
      const response = await fetch(`/api/users/${userId}`);
      const data = await response.json();
      setUser(data);
    }

    fetchUser();
  }, [userId]); // Зависит от userId, чтобы перезагружать при изменении

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Страница пользователя: {user.name}</h1>
      {/* Отображаем данные пользователя */}
    </div>
  );
}

export default UserPage;
