import { BrowserRouter as Router, Route, Routes, useParams } from 'react-router-dom';
import axios from 'axios';
import getItems from '../../utils/getItems';

const UserPage = () => {
  const {id} = useParams();

  const [items, setItems] = useState([])

  useEffect(()=> {
      axios.get(` https://dummyjson.com/users/${id}`)
      .then(res => {
          console.log(res)
          setItems(res.data)
      })
      .catch(err =>{
          console.log(err)
      })
  }, [setItems])

console.log(items)

  return (
    <div>
      <h1>Страница пользователя {items.firstName}</h1>
      <div>
        <h2>Основная информация</h2>
        <p>Имя {items.firstName}</p>
        <p>Фамилия {items.lastName}</p>
        <p>Email {items.email}</p>
        <p>Роль {items.role}</p>
      </div>
      <div>
        {/* <h2> Информация о месте работы</h2>
        {/* <p>Отрасль {items.company.department}</p> */}
        {/* <p>Название компании "{items.company.name}"</p>
        <p>Название должности {items.company.title}</p>
        <p>Адрес {items.city}</p> */} 
      </div>
    </div>
  );
}

export default UserPage;
