import { useState } from 'react';
import ClearIcon from '../Icons/ClearIcon/ClearIcon';
import './Filter.css';
import { useModal } from '../../App';

function Filter() {
  const [inputValue, setInputValue] = useState('');

  const { modalContext, setModalContext } = useModal();

  function onInput(e) {
    const value = e.target.value;
    setInputValue(value);
    setModalContext({ ...modalContext, filterValue: value });
  }

  function clearFilter() {
    setInputValue('');
    setModalContext({ ...modalContext, filterValue: '' });
  }
  return (
    <div className="filter">
      <input
        value={inputValue}
        className="filter__input"
        placeholder='&#128269; &#160; Поиск по имени или фамилии'
        onChange={onInput} />
    </div>
  );
}

export default Filter;
